import React from 'react';
import logo from './logo.svg';
import './App.css';
import TotalsContainer from './totals-container';
import AveragesContainer from './averages-container';
import TabSwitchContainer from './tab-switch-container';
import axios from "axios";

const App = () => {
  const [state, setState] = React.useState({
    tab: 'global',
    data: null,
  });

  React.useEffect(() => {
    if (state.data === null) {
      axios.get("/stats.json").then((response) => {
        setState({ 
          data: response.data,
          tab: state.tab,
        });
      });
    }
  });

  const handleTabChange = React.useCallback((newValue) => {
    setState({
      tab: newValue,
      data: state.data,
    });
  }, []);

  const content = (data) => {
    return (
      <TabSwitchContainer onChange={handleTabChange} tab={state.tab}>
        <TotalsContainer data={data[state.tab]} />
        <AveragesContainer data={data[state.tab]} />
      </TabSwitchContainer>
    )
  }

  return (
    <div className="App">
      <header className="App-header">
        { state.data === null ? 'Loading' :  content(state.data) }
        <img src={logo} className="App-logo" alt="logo" />
      </header>
    </div>
  );
}

export default App;
