import React from 'react';
import TabSwitchContainer from './TabSwitchContainer';
import AggregatedStatsContainer from './AggregatedStatsContainer';
import PostsContainer from './PostsContainer';
import IndividualPostPopUp from './IndividualPostPopUp';
import ProfilePicker from './ProfilePicker';

import {
    getRecordsInTimeframe,
    partitionPostsByTimeframe,
} from './data-manipulations';

import './styles/MainContentContainer.css'

const MainContentContainer = ({ data }) => {
    const timeframes = data.timeframes;
    const platforms = data.platforms;

    const [state, setState] = React.useState({
        platforms: [platforms[0]],
        timeframe: timeframes[0],
        popUpPost: null,
    });

    const handlePlatformChange = React.useCallback((newValue) => {
        if (newValue === 'select all') {
            newValue = platforms;
        } else {
            newValue = [newValue]
        }

        setState((prevState) => ({
            ...prevState,
            platforms: newValue,
        }));
    }, []);

    const handleTimeFrameChange = React.useCallback((newValue) => {
        setState((prevState) => ({
            ...prevState,
            timeframe: newValue,
        }));
    }, []);

    const getPosts = () => {
        const partitionedPosts = partitionPostsByTimeframe(data.records, timeframes);

        const posts = [];
        const timeframeIndex = timeframes.indexOf(state.timeframe);

        for (let i = 0; i <= timeframeIndex; i++) {
            posts.push(...(
                partitionedPosts[timeframes[i]].filter(post => (post['Platform'] === state.platforms[0] || state.platforms[0] === 'global'))
            ));
        }

        return posts;
    };

    const setPopUpPost = React.useCallback((post) => {
        setState((prevState) => ({
            ...prevState,
            popUpPost: post,
        }));
    }, []);

    const clearPopUpPost = () => {
        setState((prevState) => ({
            ...prevState,
            popUpPost: null,
        }))
    };

    const profiles = [
        { username: 'milan' },
        { username: 'amy' },
    ];

    const recordsInTimeframe = getRecordsInTimeframe(data.records, timeframe.partitionDate);

    return (
        <div className="MainContentContainer">
            <div className="MainContentContainer-content">
                <TabSwitchContainer
                    onChange={handleTimeFrameChange}
                    currentTabs={[state.timeframe]}
                    tabList={timeframes}>
                    <div className="MainContentContainer-content-top">
                        <ProfilePicker profiles={profiles} handleProfileClick={handlePlatformChange}/>
                        <AggregatedStatsContainer
                            data={recordsInTimeframe}/>
                    </div>
                    <PostsContainer
                        posts={records}
                        headers={data.postHeaders[state.platforms[0]]}
                        graphData={data.graph[state.platforms[0]][state.timeframe]}
                        openPopUp={setPopUpPost}/>
                </TabSwitchContainer>
            </div>
            {
                (state.popUpPost !== null) && [
                    (<div className="MainContentContainer-popup-background" onClick={clearPopUpPost}/>),
                    (<div className="MainContentContainer-popup">
                        <IndividualPostPopUp post={state.popUpPost}/>
                    </div>)
                ]
            }
        </div>
    );
}

export default MainContentContainer;
