import React from 'react';
import StatContainer from './stat-container';
import TimeFramePicker from './timeframe-picker';

class AveragesContainer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            timeframe: 0
        };
    }

    handleTimeframeChange = (newValue) => {
        this.setState({
            timeframe: newValue
        });
    };

    render() {
        return(
            <div style={{ display: 'flex', border: '1px solid black', borderRadius: '5px'}}>
                <StatContainer name='Average Views' value='45'/>
                <StatContainer name='Average Engagement' value='45'/>
                <TimeFramePicker onChange={this.handleTimeframeChange}/>
            </div>
        )
    }
}

export default AveragesContainer;
