function getRecordsInTimeframe(timeframeIndex, records) {
    const nDays = timeframes[timeframeIndex].nDays;
    if (nDays === undefined) {
        return records;
    }

    const datePartition = new Date();
    datePartition.setDate(datePartition.getDate() - nDays);
    return records.filter(record => (new Date(record.date) > datePartition));
}

module.exports = getRecordsInTimeframe;
