import React from 'react';
import TabSwitchContainer from './tab-switch-container';

const SignUp = ({ handleSubmit }) => {
    const platformList = ['twitter', 'youtube', 'tiktok'];

    const [state, setState] = React.useState({
        tab: 'twitter',
        ...platformList.reduce((acc, platform) => {
            acc[`${platform}_status`] = null;
            return acc;
        }, {})
    });

    const handleTabChange = React.useCallback((newValue) => {
        setState({
            tab: newValue,
        });
    }, []);

    const userRef = React.useRef();
    const passRef = React.useRef();

    const onSubmitClick = () => {
        handleSubmit(userRef.current.value, passRef.current.value, state.tab).then((response) => {
            setState({
                ...state,
                [`${state.tab}_status`]: response.data
            })
        })
    }

    const statusMessage = () => {
        if (this.state[`${state.tab}_status`] === null) {
            return null;
        }

        return this.state[`${state.tab}_status`] ? 'Success' : 'Failure'
    }

    return (
        <TabSwitchContainer onChange={handleTabChange} currentTab={state.tab} tabList={platformList}>
            <div style={{ border:"1px solid black", borderRadius: '5px', margin: '10px', flex: 1 }}>
                <input ref={userRef} placeholder={`${state.tab}_username`}/>
                <input ref={passRef} placeholder={`${state.tab}_password`}/>
                <button onClick={onSubmitClick}>Submit</button>
                { (this.state[`${state.tab}_status`]) && 'Success'}
            </div>
        </TabSwitchContainer>
    );
}

export default SignUp;
