import React from 'react';

class TimeFramePicker extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            selection: 0
        }
    }

    onClick = (index) => {
        this.setState({
            selection: index
        })
        this.props.onChange(index)
    }

    style = (index) => {
        return (index === this.state.selection)
        ? {
            color: 'red'
        } : {
            color: 'black'
        }
    }

    render() {
        return(
            <div style={{border:"1px solid black", borderRadius: '5px', margin: '10px', flex: 1}}>
                <div onClick={() => this.onClick(0)} style={this.style(0)}> 1 Day </div>
                <div onClick={() => this.onClick(1)} style={this.style(1)}> 1 Week </div>
                <div onClick={() => this.onClick(2)} style={this.style(2)}> 1 Month </div>
                <div onClick={() => this.onClick(3)} style={this.style(3)}> 1 Year </div>
            </div>
        )
    }
}

export default TimeFramePicker;
