import React from 'react';
import TabSwitchContainer from './TabSwitchContainer';
import AggregatedStatsContainer from './AggregatedStatsContainer';
import PostsContainer from './PostsContainer';

import './styles/MainContentContainer.css'

const MainContentContainer = ({ data }) => {
    const timeframes = ['day', 'month', 'year', 'all time'];

    const [state, setState] = React.useState({
        platform: 'global',
        timeframe: 'day',
    });

    const handlePlatformChange = React.useCallback((newValue) => {
        setState((prevState) => ({
            ...prevState,
            platform: newValue,
        }));
    }, []);

    const handleTimeFrameChange = React.useCallback((newValue) => {
        setState((prevState) => ({
            ...prevState,
            timeframe: newValue,
        }));
    }, []);

    return (
        <div className="MainContentContainer">
            <TabSwitchContainer onChange={handlePlatformChange} currentTab={state.platform} tabList={Object.keys(data)}>
                <TabSwitchContainer onChange={handleTimeFrameChange} currentTab={state.timeframe} tabList={timeframes}>
                    <AggregatedStatsContainer data={data[state.platform].aggregated[state.timeframe]} />
                </TabSwitchContainer>
            </TabSwitchContainer>
        </div>
    );
}

export default MainContentContainer;
