import React from 'react';
import logo from './logo.svg';
import './App.css';
import MainContentContainer from './MainContentContainer';
import SignIn from './SignIn';
import SignUp from './SignUp';
import axios from "axios";

const App = () => {
  const [state, setState] = React.useState({
    user: null,
    data: null,
    signUpMode: false,
  });

  React.useEffect(() => {
    console.log(state)
    if (state.user != null && state.data === null) {
      axios.get(`http://localhost:8000/get-stats?email=${state.user}`).then((response) => {
        if (response.data) {
          setState({
            ...state,
            data: response.data,
          });
        }
      });
    }
  });

  const attemptSignIn = (email, password) => {
    axios.get(`http://localhost:8000/verify-user?email=${email}&pass=${password}`).then((response) => {
      if (response.data) {
        setState({
          ...state,
          user: email,
        });
      }
    });
  };

  const signUp = (email, password) => {
    axios.get(`http://localhost:8000/init-user?email=${email}&pass=${password}`).then((response) => {
      if (response.data) {
        setState({
          ...state,
          signUpMode: true,
          user: email,
        });
      }
    });
  };

  const addPlatformAccount = (username, password, platform) => {
    return axios.get(`http://localhost:8000/update-user?email=${state.user}&${platform}_user=${username}&${platform}_pass=${password}`)
  }

  let content = null;
  if (state.user === null) {
    content = (<SignIn handleSubmit={attemptSignIn} handleSignUp={signUp}/>);
  }
  else if (state.signUpMode) {
    content = (<SignUp handleSubmit={addPlatformAccount}/>);
  }
  else if (state.data === null) {
    content = 'Loading';
  }
  else {
    content = (<MainContentContainer user={state.user} data={state.data}/>)
  }

  return (
    <div className="App">
      <header className="App-header">
        { content }
        <img src={logo} className="App-logo" alt="logo" />
      </header>
    </div>
  );
}

export default App;
