import React from 'react';
import TabSwitchContainer from './TabSwitchContainer';
import AggregatedStatsContainer from './AggregatedStatsContainer';
import PostsContainer from './PostsContainer';
import IndividualPostPopUp from './IndividualPostPopUp';

import './styles/MainContentContainer.css'

const MainContentContainer = ({ data }) => {
    const timeframes = data.timeframes;
    const platforms = data.platforms;

    const [state, setState] = React.useState({
        platform: platforms[0],
        timeframe: timeframes[0],
        showPopUp: false,
    });

    const handlePlatformChange = React.useCallback((newValue) => {
        setState((prevState) => ({
            ...prevState,
            platform: newValue,
        }));
    }, []);

    const handleTimeFrameChange = React.useCallback((newValue) => {
        setState((prevState) => ({
            ...prevState,
            timeframe: newValue,
        }));
    }, []);

    const getPosts = () => {
        const posts = [];
        const timeframeIndex = timeframes.indexOf(state.timeframe);

        for (let i = 0; i <= timeframeIndex; i++) {
            posts.push(...(
                data.posts[timeframes[i]].filter(post => (post['Platform'] === state.platform || state.platform === 'global'))
            ));
        }

        return posts;
    };

    const showPostPopUp = (post) => {

    }

    return (
        <div className="MainContentContainer">
            <div className="MainContentContainer-content">
                <TabSwitchContainer onChange={handlePlatformChange} currentTab={state.platform} tabList={platforms}>
                    <TabSwitchContainer onChange={handleTimeFrameChange} currentTab={state.timeframe} tabList={timeframes}>
                        <AggregatedStatsContainer data={data.aggregated[state.platform][state.timeframe]} />
                        <PostsContainer posts={getPosts()} headers={data.postHeaders[state.platform]} graphData={data.graph[state.platform][state.timeframe]}/>
                    </TabSwitchContainer>
                </TabSwitchContainer>
            </div>
            {
                state.showPopUp && 
                <div className="MainContentContainer-popup">
                    <IndividualPostPopUp/>
                </div>
            }
        </div>
    );
}

export default MainContentContainer;
