function getAggregatedStats(records) {
    return {
        'Total Posts': recordsInTimeframe.length,
        ...getTotalAndAverageFromRecords(recordsInTimeframe)
    };
}

function getTotalAndAverageFromRecords(records) {
    const calculations = metrics.reduce((acc, metric) => {
        acc[`Total ${metric}`] = 0
        return acc
    }, {});

    const len = records.length;

    for (const record of records) {
        metrics.forEach((metric) => {
            calculations[`Total ${metric}`] += parseFloat((record[metric]));
        });
    }

    metrics.forEach((metric) => {
        calculations[`Average ${metric}`] = (len === 0) ? 0 : calculations[`Total ${metric}`] / len
    });

    return calculations;
}

module.exports = getAggregatedStats;
