import React from 'react';
import { Button } from '@mui/material';

import './styles/ProfilePicker.css';

const ProfilePicker = ({ profiles, selectedProfileNames, handleProfileClick }) => {

    const onProfileClick = (profile) => {
        // deep copy selectedProfileNames
        const finalProfiles = [...selectedProfileNames];

        if (finalProfiles.includes(profile)) {
            finalProfiles.splice(finalProfiles.indexOf(profile), 1);
        } else {
            finalProfiles.push(profile)
        }
        finalProfiles.push(profile)
    }

    const selectedStyle = (profile) => {
        if (selectedProfiles.includes(profile)) {
            return {
                backgroundColor: 'white',
            }
        }
    }

    return (
        <div className="ProfilePicker">
            <div className="ProfilePicker-button">
                <Button>Select All</Button> <Button>Select None</Button>
            </div>
            {
                profiles.map((profile, index) => (
                    <div className="ProfilePicker-profile"
                        key={index}
                        onClick={() => {onProfileClick(profile.username)}}
                        style={selectedStyle(profile.username)}>
                        <div className="ProfilePicker-profile-platform">

                        </div>
                        <div className="ProfilePicker-profile-details">
                            <div className="ProfilePicker-profile-pic"/>
                            <div className="ProfilePicker-profile-cover"/>
                            <div className="ProfilePicker-profile-username">
                                {profile.username}
                            </div>
                        </div>
                    </div>
                ))
            }
        </div>
    );
}

export default ProfilePicker;
