import React from 'react';
import TabSwitchContainer from './TabSwitchContainer';
import PostsContainerPostsView from './PostsContainerPostsView';
import './styles/PostsContainer.css'

const PostsContainer = ({ posts }) => {
    const views = ['graph', 'posts'];

    const [state, setState] = React.useState({
        view: 'graph',
    });

    const handleViewChange = React.useCallback((newValue) => {
        setState((prevState) => ({
            ...prevState,
            view: newValue,
        }));
    }, []);

    const cleanPosts = () => {
        return posts.map(post => ({
            Link: post.id,
            Views: post.views,
            Engagement: post.engagements,
            comments: post.comments,
            Date: post.date,
        }))
    };

    return (
        <div className="PostsContainer">
            <TabSwitchContainer onChange={handleViewChange} currentTab={state.view} tabList={views}>
                { state.view = 'posts' ? <PostsContainerPostsView posts={cleanPosts()}/> : <div></div> }
            </TabSwitchContainer>
        </div>
    );
}

export default PostsContainer;
