import React from 'react';

const  TabSwitchContainer = ({ tabList, currentTab, onChange, children }) => {
    const onClick = (value) => {
        onChange(value);
    }

    const style = (value) => {
        const activeStyles = (value === currentTab)
            ? {
                color: 'red'
            } : {
                color: 'black'
            };

        return {
            flex: 1,
            ...activeStyles,
        };
    }

    return (
        <div style={ {width:'100%', margin:'100px', border: '1px solid blac', borderRadius: '5px'} }>
            <div style={{border:"1px solid black", borderRadius: '5px', margin: '10px', display: 'flex'}}>
                { tabList.map((tab) => (
                    <div onClick={() => onClick(tab)} style={style(tab)}>{tab}</div>
                ))}
            </div>
            <div>
                {children}
            </div>
        </div>
    );
}

export default TabSwitchContainer;
