import React from 'react';
import StatContainer from './StatContainer';
import TimeFramePicker from './TimeframePicker';

const AggregatedStatsContainer = ({ data }) => {
    const [state, setState] = React.useState({
        timeframe: 'day',
    });

    const handleTimeframeChange = React.useCallback((newValue) => {
        setState({
            timeframe: newValue
        });
    }, []);

    return (
        <div>
            <div style={{ display: 'flex', border: '1px solid black', borderRadius: '5px'}}>
                <StatContainer name='Number of Posts' value={5}/>
                <StatContainer name='Total Views' value={data[state.timeframe].total_views}/>
                <StatContainer name='Total Engagement' value={data[state.timeframe].total_engagements}/>
            </div>
            <div style={{ display: 'flex', border: '1px solid black', borderRadius: '5px'}}>
                <StatContainer name='Average Views' value={data[state.timeframe].avg_views}/>
                <StatContainer name='Average Engagement' value={data[state.timeframe].avg_engagements}/>
                <StatContainer name='Average Engagement' value={data[state.timeframe].avg_engagements}/>
            </div>
        </div>
    )
}

export default AggregatedStatsContainer;
