import React from 'react';
import logo from './logo.svg';
import './App.css';
import MainContentContainer from './MainContentContainer';
import SignIn from './SignIn';
import axios from "axios";

const App = () => {
  const [state, setState] = React.useState({
    user: null,
    data: null,
  });

  const attemptSignIn = (email, password) => {
    axios.get(`https://localhost:8000/verify-user?email=${email}&pass=${password}`).then((response) => {
      console.log(response.data)
      if (response.data) {
        setState({
          user: email,
          ...state,
        });
      }
    })
  };

  let content = null;
  if (state.user === null) {
    content = (<SignIn handleSubmit={attemptSignIn}/>);
  }
  else if (state.data === null) {
    content = 'Loading';
  }
  else {
    content = (<MainContentContainer user={state.user} data={state.data}/>)
  }

  return (
    <div className="App">
      <header className="App-header">
        { content }
        <img src={logo} className="App-logo" alt="logo" />
      </header>
    </div>
  );
}

export default App;
