import React from 'react';
import TabSwitchContainer from './tab-switch-container';

const SignUp = ({ handleSubmit }) => {
    const [state, setState] = React.useState({
        tab: 'twitter',
    });

    const handleTabChange = React.useCallback((newValue) => {
        setState({
            tab: newValue,
        });
    }, []);

    const tabList = ['twitter', 'youtube', 'tiktok'];

    const userRef = React.useRef();
    const passRef = React.useRef();

    const onSubmitClick = () => {
        handleSubmit(userRef.current.value, passRef.current.value, state.tab);
    }

    return (
        <TabSwitchContainer onChange={handleTabChange} currentTab={state.tab} tabList={tabList}>
            <div style={{ border:"1px solid black", borderRadius: '5px', margin: '10px', flex: 1 }}>
                <input ref={userRef} placeholder="email"/>
                <input ref={passRef} placeholder="password"/>
                <button onClick={onSubmitClick}>Submit</button>
            </div>
        </TabSwitchContainer>
    );
}

export default SignUp;
