function partitionPostsByTimeframe(records, timeframes) {
    // Stamp each record with it's platform
    const formattedRecords = {};

    for (const platform of Object.keys(records)) {
        formattedRecords[platform] = records[platform].map(record => formatRecord(record, platform));
    }

    const flattened = Object.values(formattedRecords).flat();
    const datePartitions = timeframes.map(timeframe => timeframe.partitionDate);
    flattened.sort((a, b) => {
        return (new Date(b['Date']) - new Date(a['Date']))
    });

    const result = timeframes.reduce((acc, { name }) => {
        acc[name] = [];
        return acc;
    }, {});

    let curPartition = 0;

    flattened.forEach((record) => {
        if (datePartitions[curPartition] != null && new Date(record['Date']) < datePartitions[curPartition]) {
            ++curPartition;
        }

        result[timeframes[curPartition].name].push(record);
    });

    return result;
}

module.exports = partitionPostsByTimeframe;
