import React from 'react';

class TabSwitchContainer extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            selection: 0
        }
    }

    onClick = (index) => {
        console.log('hi', index)
        this.setState({
            selection: index
        })
    }

    style = (index) => {
        const activeStyles = (index === this.state.selection)
        ? {
            color: 'red'
        } : {
            color: 'black'
        }

        return {
            flex: 1,
            ...activeStyles,
        }
    }

    render() {
        return(
            <div style={ {width:'100%', margin:'100px', border: '1px solid blac', borderRadius: '5px'} }>
                <div style={{border:"1px solid black", borderRadius: '5px', margin: '10px', display: 'flex'}}>
                    <div onClick={() => this.onClick(0)} style={this.style(0)}> Global </div>
                    <div onClick={() => this.onClick(1)} style={this.style(1)}> Twitter </div>
                    <div onClick={() => this.onClick(2)} style={this.style(2)}> Youtube </div>
                    <div onClick={() => this.onClick(3)} style={this.style(3)}> TikTok </div>
                </div>
                <div>
                    {this.props.children}
                </div>
            </div>
        )
    }
}

export default TabSwitchContainer;
