import React from 'react';

class TabSwitchContainer extends React.Component {
    onClick = (value) => {
        this.props.onChange(value);
    }

    style = (value) => {
        const activeStyles = (value === this.props.tab)
            ? {
                color: 'red'
            } : {
                color: 'black'
            };

        return {
            flex: 1,
            ...activeStyles,
        };
    }

    render() {
        return(
            <div style={ {width:'100%', margin:'100px', border: '1px solid blac', borderRadius: '5px'} }>
                <div style={{border:"1px solid black", borderRadius: '5px', margin: '10px', display: 'flex'}}>
                    <div onClick={() => this.onClick('global')} style={this.style('global')}> Global </div>
                    <div onClick={() => this.onClick('twitter')} style={this.style('twitter')}> Twitter </div>
                    <div onClick={() => this.onClick('youtube')} style={this.style('youtube')}> Youtube </div>
                    <div onClick={() => this.onClick('tiktok')} style={this.style('tiktok')}> TikTok </div>
                </div>
                <div>
                    {this.props.children}
                </div>
            </div>
        );
    }
}

export default TabSwitchContainer;
