import React from 'react';

class TimeFramePicker extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            selection: 'day'
        }
    }

    onClick = (value) => {
        this.setState({
            selection: value
        })
        this.props.onChange(value)
    }

    style = (value) => {
        return (value === this.state.selection)
        ? {
            color: 'red'
        } : {
            color: 'black'
        }
    }

    render() {
        return(
            <div style={{border:"1px solid black", borderRadius: '5px', margin: '10px', flex: 1}}>
                <div onClick={() => this.onClick('day')} style={this.style('day')}> 1 Day </div>
                <div onClick={() => this.onClick('week')} style={this.style('week')}> 1 Week </div>
                <div onClick={() => this.onClick('month')} style={this.style('month')}> 1 Month </div>
                <div onClick={() => this.onClick('year')} style={this.style('year')}> 1 Year </div>
            </div>
        )
    }
}

export default TimeFramePicker;
