import React from 'react';
import TabSwitchContainer from './TabSwitchContainer';
import AggregatedStatsContainer from './AggregatedStatsContainer';
import PostsContainer from './PostsContainer';
import IndividualPostPopUp from './IndividualPostPopUp';
import ProfilePicker from './ProfilePicker';

import {
    getRecordsInTimeframe,
    getGraphData,
    getAggregatedStats,
} from './data-manipulations';

import './styles/MainContentContainer.css';

const MainContentContainer = ({ data }) => {
    const timeframeNames = data.timeframes.map(timeframe => timeframe.name);
    const platforms = data.platforms;

    const [state, setState] = React.useState({
        platforms: [platforms[0]],
        timeframe: timeframeNames[0],
        popUpPost: null,
    });

    const handlePlatformChange = React.useCallback((newValue) => {
        if (newValue === 'select all') {
            newValue = platforms;
        } else {
            newValue = [newValue]
        }

        setState((prevState) => ({
            ...prevState,
            platforms: newValue,
        }));
    }, []);

    const handleTimeFrameChange = React.useCallback((newValue) => {
        setState((prevState) => ({
            ...prevState,
            timeframe: newValue,
        }));
    }, []);

    const setPopUpPost = React.useCallback((post) => {
        setState((prevState) => ({
            ...prevState,
            popUpPost: post,
        }));
    }, []);

    const clearPopUpPost = () => {
        setState((prevState) => ({
            ...prevState,
            popUpPost: null,
        }))
    };

    const profiles = [
        { username: 'milan' },
        { username: 'amy' },
    ];

    const recordsInTimeframe = getRecordsInTimeframe(data.records, data.timeframe.partitionDate);
    console.log('records')
    console.log(recordsInTimeframe);
    const aggregatedData = getAggregatedStats(recordsInTimeframe, data.metrics);
    console.log('aggregated')
    console.log(aggregatedData)
    const graphData = getGraphData(recordsInTimeframe, data.timeframe.graphPartitions);
    console.log('graph')
    console.log(graphData)

    const postHeaders = (new Set(data.profiles.map(profile => profile.platform)).size > 1)
        ? data.postHeaders['global']
        : data.postHeaders[data.profiles[0].platform]

    console.log('headers')
    console.log(postHeaders)

    return (
        <div className="MainContentContainer">
            <div className="MainContentContainer-content">
                <TabSwitchContainer
                    onChange={handleTimeFrameChange}
                    currentTabs={[state.timeframe]}
                    tabList={timeframeNames}>
                    <div className="MainContentContainer-content-top">
                        <ProfilePicker profiles={profiles} handleProfileClick={handlePlatformChange}/>
                        <AggregatedStatsContainer
                            data={aggregatedData}/>
                    </div>
                    <PostsContainer
                        posts={recordsInTimeframe}
                        headers={postHeaders}
                        graphData={graphData}
                        openPopUp={setPopUpPost}/>
                </TabSwitchContainer>
            </div>
            {
                (state.popUpPost !== null) && [
                    (<div className="MainContentContainer-popup-background" onClick={clearPopUpPost}/>),
                    (<div className="MainContentContainer-popup">
                        <IndividualPostPopUp post={state.popUpPost}/>
                    </div>)
                ]
            }
        </div>
    );
}

export default MainContentContainer;
