import React from 'react';
import logo from './logo.svg';
import './App.css';
import TotalsContainer from './totals-container';
import AveragesContainer from './averages-container';
import TabSwitchContainer from './tab-switch-container';
import axios from "axios";

const App = () => {
  const [state, setState] = React.useState({
    tab: 'global',
    data: null,
  });

  React.useEffect(() => {
    axios.get("/stats.json").then((response) => {
      setState({ 
        data: response.data,
        tab: state.data,
      });
    });
  })

  const handleTabChange = React.useCallback((newValue) => {
    setState({
      tab: newValue,
      data: state.data,
    });
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        { state.data === null ? 'Loading' : (
          <TabSwitchContainer onChange={handleTabChange}>
            <TotalsContainer data={state.data[state.tab]} />
            <AveragesContainer data={state.data[state.tab]} />
          </TabSwitchContainer>
        ) }
        <img src={logo} className="App-logo" alt="logo" />
      </header>
    </div>
  );
}

export default App;
