import React from 'react';
import logo from './logo.svg';
import './styles/App.css';
import MainContentContainer from './MainContentContainer';
import SignIn from './SignIn';
import AddPlatformSelection from './AddPlatformSelection';
import axios from "axios";

const App = () => {
  const [state, setState] = React.useState({
    user: null,
    data: null,
    signUpMode: false,
    screen: 'sign-in'
  });

  React.useEffect(() => {
    if (state.user != null && state.data === null) {
      axios.get(`http://localhost:8000/get-data?username=${state.user}`).then((response) => {
        if (response.data) {
          setState({
            ...state,
            data: response.data,
          });
        }
      });
    }
  });

  const attemptSignIn = React.useCallback((username, password) => {
    axios.get(`http://localhost:8000/verify-user?username=${username}&pass=${password}`).then((response) => {
      if (response.data) {
        setState((prevState) => ({
          ...prevState,
          user: username,
        }));
      }
    });
  }, []);

  const signUp = React.useCallback((username, password) => {
    axios.get(`http://localhost:8000/init-user?username=${username}&pass=${password}`).then((response) => {
      if (response.data) {
        setState((prevState) => ({
          ...prevState,
          signUpMode: true,
          user: username,
        }));
      }
    });
  }, []);

  const addPlatformAccount = React.useCallback((username, password, platform) => {
    return axios.get(`http://localhost:8000/update-user?username=${state.user}&${platform}_user=${username}&${platform}_pass=${password}`)
  }, [state]);

  const finishSignUp = React.useCallback(() => {
    axios.get(`http://localhost:8000/run-scrapers?username=${state.user}`)

    setState((prevState) => ({
      ...prevState,
      signUpMode: false,
    }));
  }, [state]);

  const getContent = () => {
    switch(state.screen) {
      case 'sign-in':
        return (<SignIn handleSubmit={attemptSignIn} handleSignUp={signUp}/>);
    }

    if (state.user === null) {

    }

    if (state.signUpMode) {
      return (<AddPlatformSelection handleSubmit={addPlatformAccount} handleContinue={finishSignUp}/>);
    }

    if (state.data === null) {
      return 'Loading';
    }

    return (<MainContentContainer user={state.user} data={state.data}/>)
  }


  return (
    <div className="App">
      <header className="App-header">
        {/* <img src={logo} className="App-logo" alt="logo" /> */}
      </header>
      { getContent() }
    </div>
  );
}

export default App;
