        handleSubmit(userRef.current.value, passRef.current.value);
    };

    const onSignUpClick = () => {
        handleSignUp(userRef.current.value, passRef.current.value);
    };

    return (
        <div className="AddPlatform">
            <div className="AddPlatform-instructions">

            </div>
            <div className="AddPlatform-form">
                <div className="AddPlatform-header">
                    Add {platform} account
                </div>
                <div className="AddPlatform-username">
                    <TextField inputRef={userRef} variant="outlined" label="email"/>
                </div>
                <div className="AddPlatform-button">
                    <Button onClick={onSubmitClick}>Sign In</Button>
                </div>
            </div>
        </div>
    );
}

export default AddPlatform;
