import React from 'react';
import TabSwitchContainer from './TabSwitchContainer';
import { Button } from '@mui/material';

import './styles/AddPlatformSelection.css';

const AddPlatformSelection = ({ handleSubmit, handleContinue }) => {
    const platformList = ['twitter', 'youtube', 'tiktok'];
    const userRef = React.useRef();
    const passRef = React.useRef();

    const [state, setState] = React.useState({
        tab: 'twitter',
        ...platformList.reduce((acc, platform) => {
            acc[`${platform}_status`] = null;
            return acc;
        }, {})
    });

    const handleTabChange = React.useCallback((newValue) => {
        setState((prevState) => ({
            ...prevState,
            tab: newValue,
        }));
    }, []);

    const onSubmitClick = () => {
        handleSubmit(userRef.current.value, passRef.current.value, state.tab).then((response) => {
            setState((prevState) => ({
                ...prevState,
                [`${prevState.tab}_status`]: response.data
            }));
        })
    }

    const onContinueClick = () => {
        handleContinue();
    }

    const statusMessage = () => {
        if (state[`${state.tab}_status`] === null) {
            return null;
        }

        return state[`${state.tab}_status`] ? 'Success' : 'Failure'
    }

    return (
        <div className="AddPlatformSelection">
            <div className="AddPlatformSelection-platforms">
                {
                    platformList.map((platform) => (
                        <div className="AddPlatformSelection-platform">
                            <div className="AddPlatformSelection-platform-logoName">
                                <div className="AddPlatformSelection-platform-logo">
                                    hi
                                </div>
                                <div className="AddPlatformSelection-platform-name">
                                    { platform }
                                </div>
                            </div>
                            <div className="AddPlatformSelection-platform-plus">
                                +
                            </div>
                        </div>
                    ))
                }
                {/* <TabSwitchContainer onChange={handleTabChange} currentTab={state.tab} tabList={platformList}>
                    <div style={{ border:"1px solid black", borderRadius: '5px', margin: '10px', flex: 1 }}>
                        <input ref={userRef} placeholder={`${state.tab}_username`}/>
                        <input ref={passRef} placeholder={`${state.tab}_password`}/>
                        <button onClick={onSubmitClick}>Submit</button>
                        { statusMessage() }
                    </div>
                </TabSwitchContainer>
                <button onClick={onContinueClick}>Continue</button> */}
            </div>
            <div className="AddPlatformSelection-button">
                <Button></Button>
            </div>
        </div>
    );
}

export default AddPlatformSelection;
