import React from 'react';
import logo from './logo.svg';
import './App.css';
import TotalsContainer from './totals-container';
import AveragesContainer from './averages-container';
import TabSwitchContainer from './tab-switch-container';

const App = ({ data }) => {
  const [state, setState] = React.useState({
    tab: 'global',
  });

  const handleTabChange = React.useCallback((newValue) => {
    setState({
      tab: newValue,
    });
  }, []);

  return (
    <div className="App">
      <header className="App-header">
          <TabSwitchContainer onChange={handleTabChange} tab={state.tab}>
            <TotalsContainer data={data[state.tab]} />
            <AveragesContainer data={data[state.tab]} />
          </TabSwitchContainer>
        <img src={logo} className="App-logo" alt="logo" />
      </header>
    </div>
  );
}

export default App;
