import React from 'react';

const  TabSwitchContainer = ({ tab, onChange, children }) => {
    const onClick = (value) => {
        onChange(value);
    }

    const style = (value) => {
        const activeStyles = (value === tab)
            ? {
                color: 'red'
            } : {
                color: 'black'
            };

        return {
            flex: 1,
            ...activeStyles,
        };
    }

    return (
        <div style={ {width:'100%', margin:'100px', border: '1px solid blac', borderRadius: '5px'} }>
            <div style={{border:"1px solid black", borderRadius: '5px', margin: '10px', display: 'flex'}}>
                <div onClick={() => onClick('global')} style={style('global')}> Global </div>
                <div onClick={() => onClick('twitter')} style={style('twitter')}> Twitter </div>
                <div onClick={() => onClick('youtube')} style={style('youtube')}> Youtube </div>
                <div onClick={() => onClick('tiktok')} style={style('tiktok')}> TikTok </div>
            </div>
            <div>
                {children}
            </div>
        </div>
    );
}

export default TabSwitchContainer;
