import React from 'react';
import StatContainer from './stat-container';
import TimeFramePicker from './timeframe-picker';

class TotalsContainer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            timeframe: 0
        };
    }

    handleTimeframeChange = React.useCallback((newValue) => {
        this.setState({
            timeframe: newValue
        });
    }, []);

    render() {
        return(
            <div style={{ display: 'flex', border: '1px solid black', borderRadius: '5px'}}>
                <StatContainer name='Total Views' value='45'/>
                <StatContainer name='Total Engagement' value='45'/>
                <TimeFramePicker onChange={this.handleTimeframeChange}/>
            </div>
        )
    }
}

export default TotalsContainer;
